typeProperties.script:
section Section1;
shared Emp1 = let AdfDoc = AzureStorage.DataLakeContents("https://dl482891.dfs.core.windows.net/source/emp.csv"),Csv = Csv.Document(AdfDoc, [Delimiter = ",", Encoding = TextEncoding.Utf8, QuoteStyle = QuoteStyle.Csv]), PromotedHeaders = Table.PromoteHeaders(Csv, [PromoteAllScalars = true]) in  PromotedHeaders;
shared UserQuery = let Source = #"Emp1",
  #"Changed column type" = Table.TransformColumnTypes(Source, {{"empid", Int64.Type}, {"salary", Int64.Type}, {"did", Int64.Type}}),
  #"Removed duplicates" = Table.Distinct(#"Changed column type", {"empid"}),
  #"Inserted merged column" = Table.AddColumn(#"Removed duplicates", "Eid-Did", each Text.Combine({Text.From([empid]), Text.From([did])}, "-"), type text),
  #"Capitalized each word" = Table.TransformColumns(#"Inserted merged column", {{"empname", each Text.Proper(_), type nullable text}}),
  #"Trimmed text" = Table.TransformColumns(#"Capitalized each word", {{"empname", each Text.Trim(_), type nullable text}}),
  #"Inserted text length" = Table.AddColumn(#"Trimmed text", "Length", each Text.Length([#"Eid-Did"]), type number),
  #"Renamed columns" = Table.RenameColumns(#"Inserted text length", {{"Length", "EIDDIDLen"}}) in #"Renamed columns";
